import React from "react";
import classnames from "classnames/bind";
import styles from "./App.module.scss";
import { render } from 'react-dom'
import { Provider } from 'react-redux'
import { createStore } from 'redux'
import { connect } from 'react-redux'

const cx = classnames.bind(styles);

const defaultState = [];

const cmp = (index) => {
  return (a, b) => {
    if (a[index] > b[index]) {
      return 1;
    }
    return -1;
  }
}

const rootReducer = (state = defaultState, action) => {
  switch (action.type) {
    case 'ADD': {
      return state => (state.push(action.payload));
    }
    case 'SORT': {
      if (state.payload == 'NAME') {
        return state => this.state.sort(this.cmp('name'))
      } else {
        return state => this.state.sort(this.cmp('priority'))
      }
    }
    default: {
      return state;
    }
  }
}

const store = createStore(rootReducer);

const addTask = (task) => ({
  type: 'ADD',
  payload: task
});

const sort = (id) => ({
  type: 'SORT',
  payload: id // 'PRIORITY' or 'NAME'
});

const App = () => (
  <Provider store={store}>
    <MainComponent />
  </Provider>
);

const mapStateToProps = store => ({ });

const mapDispatchToProps = dispatch => ({
  dispatcherADD: (task) => dispatch(addTask(task)),
  dispatcherSORT: (id) => dispatch(sort(id))
});

const MainComponent = ({ dispatcherADD, dispatcherSORT }) => {
  const state = {
    id: 1,
    name: 'Task name',
    description: 'Task description',
    priority: 123
  };

  const handleChangeName = (event) => {
    if (event === undefined) return;
    const newName = event.target.value;
    this.setState(state => ({ name: newName }));
  };

  const handleChangeDescription = (event) => {
    if (event === undefined) return;
    const newDescription = event.target.value;
    this.setState(state => ({ description: newDescription }));
  };

  const handleChangePriority = (event) => {
    if (event === undefined) return;
    const newPriority = event.target.value;
    this.setState(state => ({ priority: parseInt(newPriority) }));
  };

  const NewTask = () => {
    this.setState(state => ({ id: state.id + 1 }));
    const newTask = {
      id: this.state.id,
      name: this.state.name,
      description: this.state.description,
      priority: this.state.priority
    }
    return newTask;
  };

  const TaskShow = ({ task }) => (
    <div className={cx("taskLine")}>
      <div className={cx("taskId")}>
        #{task.id}
      </div>
      <div className={cx("taskName")}>
        {task.name}
      </div>
      <div className={cx("taskDescription")}>
        {task.description}
      </div>
      <div className={cx("taskPriority")}>
        {task.priority}
      </div>
    </div>
  );

  return (
    <div className={cx("container")}>
      <div className={cx("header")}>
        <h1>Task Manager</h1>
      </div>
      <div className={cx("input")}>
        <div className={cx("boxes")}>
          <div className={cx("box")}>
            <h2>Name</h2>
            <input onChange={this.handleChangeName}/>
          </div>
          <div className={cx("box")}>
            <h2>Discription</h2>
            <input onChange={this.handleChangeDescription}/>
          </div>
          <div className={cx("box")}>
            <h2>Priority</h2>
            <input onChange={this.handleChangePriority}/>
          </div>
        </div>
        <div className={cx("buttons")}>
          <button className={cx("button")} onClick={() => dispatcherADD(this.NewTask)}>Add New Task</button>
        </div>
      </div>
      <div className={cx("buttons")}>
          <button className={cx("button")} onClick={() => dispatcherSORT('NAME')}>Sort by Name</button>
          <button className={cx("button")} onClick={() => dispatcherSORT('PRIORITY')}>Sort by Priority</button>
      </div>
      <div className={cx("content")}>
        {this.state.tasks.map(task => this.TaskShow({task}))}
      </div>
    </div>
  );
}


connect(mapStateToProps, mapDispatchToProps)(MainComponent);
export default App;
